import React from 'react';
import '../styles/hero.css';

function Hero() {
  return (
    <section className="hero-container">
      <div className="hero-content">
        <p className="hero-greeting">Hi, I am Sara <span className="phonetic">(saa-ruh)</span></p>
        <h1 className="hero-headline">
          I craft impactful data-driven <br />
          solutions & intelligent systems <br />
          that <span className="highlight">empower people</span> ✦
        </h1>
        <p className="hero-subheading">
          Master’s in Computer Science — Data Science @ <span className="bold">Trinity College Dublin</span>
        </p>
      </div>
    </section>
  );
}

export default Hero;
