// src/components/Projects.js

import React, { useEffect, useState } from 'react';
import ProjectCard from './ProjectCard';
// import { fetchGitHubRepos } from '../utils/api'; // We'll define this
import '../styles/projects.css';


function Projects() {
  // const [repos, setRepos] = useState([]);

  // useEffect(() => {
  //   fetchGitHubRepos()
  //     .then(data => setRepos(data))
  //     .catch(err => console.error(err));
  // }, []);

  const projects = [
    {
      title: 'Sales Insights',
      description: 'An interactive dashboard to track KPIs and sales trends.',
      tags: ['DataViz', 'Power BI', 'Storytelling'],
      image: '/assets/sales-insights.png',
      link: 'https://example.com/sales',
    },
    {
      title: 'JPMC Anubhuti',
      description: 'Streamlined data visualizations to support NGO program analysis.',
      tags: ['Impact', 'Visualization', 'NGO'],
      image: '/assets/anubhuti.png',
      link: 'https://example.com/anubhuti',
    },
    // Add more manually
  ];
  
  return (
    <section className="projects-section" id="projects">
      <h2>My Projects</h2>
      <div className="projects-grid">
        {projects.map((project, i) => (
          <ProjectCard key={i} project={project} />
        ))}
      </div>
    </section>
  );
}

export default Projects;
